import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const EditProduct = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [sellers, setSellers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState('');
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    price: '',
    category: '',
    stock: '',
    imageUrl: '',
    sellerId: ''
  });

  useEffect(() => {
    const fetchProductAndSellers = async () => {
      try {
        // In real app, fetch from API
        // const productResponse = await axios.get(`http://localhost:5000/api/products/${id}`);
        // const sellersResponse = await axios.get('http://localhost:5000/api/sellers');

        // setFormData({
        //   name: productResponse.data.name,
        //   description: productResponse.data.description,
        //   price: productResponse.data.price,
        //   category: productResponse.data.category,
        //   stock: productResponse.data.stock,
        //   imageUrl: productResponse.data.imageUrl,
        //   sellerId: productResponse.data.sellerId
        // });

        // setSellers(sellersResponse.data);

        // Mock data
        setFormData({
          name: 'Herbal Shampoo',
          description: 'Natural ayurvedic shampoo for hair growth with herbs and oils.',
          price: '1200.50',
          category: 'Hair Care',
          stock: '45',
          imageUrl: 'https://images.unsplash.com/photo-1608571423902-eed4a5ad8108?ixlib=rb-1.2.1&auto=format&fit=crop&w=600&q=80',
          sellerId: '101'
        });

        setSellers([
          {
            _id: '101',
            name: 'Herbal Distributors Pvt Ltd'
          },
          {
            _id: '102',
            name: 'Ayurveda Wellness Products'
          },
          {
            _id: '103',
            name: 'Natural Health Solutions'
          },
          {
            _id: '104',
            name: 'Traditional Remedies Inc'
          },
          {
            _id: '105',
            name: 'Green Leaf Herbs'
          }
        ]);

        setLoading(false);
      } catch (error) {
        console.error('Error fetching product or sellers:', error);
        setError('Failed to load product details. Please try again later.');
        setLoading(false);
      }
    };

    fetchProductAndSellers();
  }, [id]);

  const { name, description, price, category, stock, imageUrl, sellerId } = formData;

  const onChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const onSubmit = async e => {
    e.preventDefault();

    // Validation
    if (!name || !description || !price || !category || !stock || !imageUrl || !sellerId) {
      setError('Please fill in all fields');
      return;
    }

    setSubmitting(true);
    setError('');

    try {
      // For a real app, submit to API
      // const updatedProduct = {
      //   name,
      //   description,
      //   price: parseFloat(price),
      //   category,
      //   stock: parseInt(stock),
      //   imageUrl,
      //   sellerId
      // };

      // await axios.put(`http://localhost:5000/api/products/${id}`, updatedProduct);

      // For demo purposes
      console.log('Updating product:', id, { name, description, price, category, stock, imageUrl, sellerId });

      // Simulate API call
      setTimeout(() => {
        setSubmitting(false);
        navigate(`/products/${id}`);
      }, 1000);

    } catch (err) {
      setError(err.response?.data?.message || 'Failed to update product. Please try again.');
      setSubmitting(false);
    }
  };

  const categories = [
    'Hair Care',
    'Skin Care',
    'Digestive Health',
    'Immunity Boosters',
    'Oral Care',
    'Sleep & Relaxation',
    'Joint & Muscle Care',
    'Women\'s Health',
    'Men\'s Health',
    'Weight Management'
  ];

  if (loading) {
    return (
      <div className="text-center py-12">
        <div className="animate-spin rounded-full h-16 w-16 border-t-4 border-green-700 border-opacity-50 mx-auto"></div>
        <p className="mt-4 text-gray-600">Loading product details...</p>
      </div>
    );
  }

  return (
    <div>
      <h1 className="text-3xl font-bold text-green-800 mb-8">Edit Product</h1>

      {error && (
        <div className="bg-red-100 text-red-700 p-4 rounded-lg mb-6">
          <p>{error}</p>
        </div>
      )}

      <div className="bg-white rounded-lg shadow-md overflow-hidden">
        <form onSubmit={onSubmit} className="p-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <label className="block text-gray-700 font-medium mb-2" htmlFor="name">
                Product Name
              </label>
              <input
                type="text"
                id="name"
                name="name"
                value={name}
                onChange={onChange}
                className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500"
                placeholder="Enter product name"
                required
              />
            </div>

            <div>
              <label className="block text-gray-700 font-medium mb-2" htmlFor="category">
                Category
              </label>
              <select
                id="category"
                name="category"
                value={category}
                onChange={onChange}
                className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500"
                required
              >
                <option value="">Select a category</option>
                {categories.map((cat) => (
                  <option key={cat} value={cat}>{cat}</option>
                ))}
              </select>
            </div>

            <div>
              <label className="block text-gray-700 font-medium mb-2" htmlFor="price">
                Price (Rs.)
              </label>
              <input
                type="number"
                step="0.01"
                min="0"
                id="price"
                name="price"
                value={price}
                onChange={onChange}
                className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500"
                placeholder="Enter price"
                required
              />
            </div>

            <div>
              <label className="block text-gray-700 font-medium mb-2" htmlFor="stock">
                Stock Quantity
              </label>
              <input
                type="number"
                min="0"
                id="stock"
                name="stock"
                value={stock}
                onChange={onChange}
                className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500"
                placeholder="Enter stock quantity"
                required
              />
            </div>

            <div>
              <label className="block text-gray-700 font-medium mb-2" htmlFor="imageUrl">
                Image URL
              </label>
              <input
                type="text"
                id="imageUrl"
                name="imageUrl"
                value={imageUrl}
                onChange={onChange}
                className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500"
                placeholder="Enter image URL"
                required
              />
            </div>

            <div>
              <label className="block text-gray-700 font-medium mb-2" htmlFor="sellerId">
                Seller
              </label>
              <select
                id="sellerId"
                name="sellerId"
                value={sellerId}
                onChange={onChange}
                className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500"
                required
              >
                <option value="">Select a seller</option>
                {sellers.map((seller) => (
                  <option key={seller._id} value={seller._id}>
                    {seller.name}
                  </option>
                ))}
              </select>
            </div>

            <div className="col-span-1 md:col-span-2">
              <label className="block text-gray-700 font-medium mb-2" htmlFor="description">
                Description
              </label>
              <textarea
                id="description"
                name="description"
                value={description}
                onChange={onChange}
                rows="4"
                className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500"
                placeholder="Enter product description"
                required
              ></textarea>
            </div>
          </div>

          <div className="mt-8 flex justify-between">
            <button
              type="button"
              onClick={() => {
                if (window.confirm('Are you sure you want to delete this product?')) {
                  // In a real app, delete via API
                  // await axios.delete(`http://localhost:5000/api/products/${id}`);
                  console.log(`Delete product ${id}`);
                  navigate('/products');
                }
              }}
              className="bg-red-600 hover:bg-red-700 text-white px-5 py-2 rounded-lg"
            >
              Delete
            </button>

            <div>
              <button
                type="button"
                onClick={() => navigate(`/products/${id}`)}
                className="bg-gray-300 hover:bg-gray-400 text-gray-800 px-5 py-2 rounded-lg mr-4"
              >
                Cancel
              </button>
              <button
                type="submit"
                className="bg-green-700 hover:bg-green-800 text-white px-5 py-2 rounded-lg"
                disabled={submitting}
              >
                {submitting ? (
                  <div className="flex items-center">
                    <div className="animate-spin rounded-full h-5 w-5 border-t-2 border-white mr-2"></div>
                    <span>Updating...</span>
                  </div>
                ) : 'Update Product'}
              </button>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
};

export default EditProduct;
