import React, { useState, useEffect, useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../../context/AuthContext';

const CartIcon = () => {
  const [cartCount, setCartCount] = useState(0);
  const { isAuthenticated, isCustomer } = useContext(AuthContext);
  const navigate = useNavigate();

  useEffect(() => {
    // Update cart count when component mounts
    updateCartCount();

    // Set up event listener for storage events
    window.addEventListener('storage', updateCartCount);

    // Custom event listener for cart updates from same window
    window.addEventListener('cartUpdated', updateCartCount);

    return () => {
      window.removeEventListener('storage', updateCartCount);
      window.removeEventListener('cartUpdated', updateCartCount);
    };
  }, []);

  const updateCartCount = () => {
    const cart = JSON.parse(localStorage.getItem('ayurvedicCart')) || [];
    console.log('CartIcon - Cart from localStorage:', cart);
    const count = cart.reduce((total, item) => total + item.quantity, 0);
    console.log('CartIcon - Cart count:', count);
    setCartCount(count);
  };

  const handleCartClick = (e) => {
    if (!isAuthenticated) {
      e.preventDefault();
      if (window.confirm('You need to log in to view your cart. Would you like to log in now?')) {
        navigate('/login', { state: { from: '/cart' } });
      }
    } else if (!isCustomer()) {
      e.preventDefault();
      alert('Only customers can access the shopping cart.');
    }
  };

  return (
    <Link to="/cart" className="relative inline-block group" onClick={handleCartClick}>
      <div className="flex flex-col items-center">
        <div className="relative p-2 rounded-full group-hover:bg-green-50 transition-colors">
          <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 text-gray-700 group-hover:text-green-600 transition-colors" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 3h2l.4 2M7 13h10l4-8H5.4M7 13L5.4 5M7 13l-2.293 2.293c-.63.63-.184 1.707.707 1.707H17m0 0a2 2 0 100 4 2 2 0 000-4zm-8 2a2 2 0 11-4 0 2 2 0 014 0z" />
          </svg>
          {cartCount > 0 && (
            <span className="absolute -top-1 -right-1 bg-green-600 text-white text-xs font-bold rounded-full h-5 w-5 flex items-center justify-center transform transition-transform group-hover:scale-110">
              {cartCount}
            </span>
          )}
        </div>
        <span className="text-xs font-medium text-gray-700 group-hover:text-green-600 transition-colors">Cart</span>
      </div>
    </Link>
  );
};

export default CartIcon;
