import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const ProductsList = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [categoryFilter, setCategoryFilter] = useState('');
  const [priceSort, setPriceSort] = useState('');

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        // In a real app, fetch from API
        // const response = await axios.get('http://localhost:5000/api/products');
        // setProducts(response.data);

        // Mock data
        setProducts([
          {
            _id: '1',
            name: 'Herbal Shampoo',
            description: 'Natural ayurvedic shampoo for hair growth with herbs and oils.',
            price: 1200.50,
            category: 'Hair Care',
            stock: 45,
            imageUrl: 'https://images.unsplash.com/photo-1608571423902-eed4a5ad8108?ixlib=rb-1.2.1&auto=format&fit=crop&w=600&q=80',
            sellerId: '101'
          },
          {
            _id: '2',
            name: 'Ayurvedic Face Cream',
            description: 'Rejuvenating face cream made with natural ingredients.',
            price: 1500.00,
            category: 'Skin Care',
            stock: 30,
            imageUrl: 'https://images.unsplash.com/photo-1601049676869-702ea24cfd58?ixlib=rb-1.2.1&auto=format&fit=crop&w=600&q=80',
            sellerId: '102'
          },
          {
            _id: '3',
            name: 'Digestive Herbal Tea',
            description: 'Soothing herbal tea to aid digestion and gut health.',
            price: 850.00,
            category: 'Digestive Health',
            stock: 60,
            imageUrl: 'https://images.unsplash.com/photo-1564890369478-c89ca6d9cde9?ixlib=rb-1.2.1&auto=format&fit=crop&w=600&q=80',
            sellerId: '103'
          },
          {
            _id: '4',
            name: 'Immunity Booster',
            description: 'Natural supplement to strengthen your immune system.',
            price: 2100.00,
            category: 'Immunity Boosters',
            stock: 25,
            imageUrl: 'https://images.unsplash.com/photo-1471864190281-a93a3070b6de?ixlib=rb-1.2.1&auto=format&fit=crop&w=600&q=80',
            sellerId: '104'
          },
          {
            _id: '5',
            name: 'Herbal Hair Oil',
            description: 'Traditional oil blend to prevent hair fall and promote growth.',
            price: 950.00,
            category: 'Hair Care',
            stock: 40,
            imageUrl: 'https://images.unsplash.com/photo-1559830772-73d4ede5bcac?ixlib=rb-1.2.1&auto=format&fit=crop&w=600&q=80',
            sellerId: '101'
          },
          {
            _id: '6',
            name: 'Ayurvedic Toothpaste',
            description: 'Natural toothpaste with neem, clove and other herbs.',
            price: 350.00,
            category: 'Oral Care',
            stock: 75,
            imageUrl: 'https://images.unsplash.com/photo-1612538498456-e861df91d4d0?ixlib=rb-1.2.1&auto=format&fit=crop&w=600&q=80',
            sellerId: '105'
          },
          {
            _id: '7',
            name: 'Herbal Sleep Aid',
            description: 'Natural supplement to improve sleep quality.',
            price: 1250.00,
            category: 'Sleep & Relaxation',
            stock: 35,
            imageUrl: 'https://images.unsplash.com/photo-1611072172377-0cabc3addb32?ixlib=rb-1.2.1&auto=format&fit=crop&w=600&q=80',
            sellerId: '102'
          },
          {
            _id: '8',
            name: 'Aloe Vera Gel',
            description: 'Pure aloe vera gel for skin care and wounds.',
            price: 750.00,
            category: 'Skin Care',
            stock: 50,
            imageUrl: 'https://images.unsplash.com/photo-1596755389378-c31d21fd1273?ixlib=rb-1.2.1&auto=format&fit=crop&w=600&q=80',
            sellerId: '103'
          }
        ]);

        setLoading(false);
      } catch (error) {
        console.error('Error fetching products:', error);
        setError('Failed to fetch products. Please try again later.');
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  // Filter and sort products
  const filteredProducts = products.filter(product => {
    return (
      product.name.toLowerCase().includes(searchTerm.toLowerCase()) &&
      (categoryFilter === '' || product.category === categoryFilter)
    );
  }).sort((a, b) => {
    if (priceSort === 'low') {
      return a.price - b.price;
    } else if (priceSort === 'high') {
      return b.price - a.price;
    }
    return 0;
  });

  // Get unique categories
  const categories = [...new Set(products.map(product => product.category))];

  if (loading) {
    return (
      <div className="text-center py-12">
        <div className="animate-spin rounded-full h-16 w-16 border-t-4 border-green-700 border-opacity-50 mx-auto"></div>
        <p className="mt-4 text-gray-600">Loading products...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-100 text-red-700 p-4 rounded-lg mb-6">
        <p>{error}</p>
      </div>
    );
  }

  return (
    <div>
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold text-green-800">Products</h1>
        <Link
          to="/products/add"
          className="bg-green-700 hover:bg-green-800 text-white px-4 py-2 rounded-lg flex items-center"
        >
          <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" viewBox="0 0 20 20" fill="currentColor">
            <path fillRule="evenodd" d="M10 3a1 1 0 011 1v5h5a1 1 0 110 2h-5v5a1 1 0 11-2 0v-5H4a1 1 0 110-2h5V4a1 1 0 011-1z" clipRule="evenodd" />
          </svg>
          Add New Product
        </Link>
      </div>

      {/* Filters */}
      <div className="bg-white rounded-lg shadow-md p-6 mb-8">
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Search
            </label>
            <input
              type="text"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              placeholder="Search products..."
              className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Category
            </label>
            <select
              value={categoryFilter}
              onChange={(e) => setCategoryFilter(e.target.value)}
              className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500"
            >
              <option value="">All Categories</option>
              {categories.map((category) => (
                <option key={category} value={category}>{category}</option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Price
            </label>
            <select
              value={priceSort}
              onChange={(e) => setPriceSort(e.target.value)}
              className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500"
            >
              <option value="">Sort by Price</option>
              <option value="low">Price: Low to High</option>
              <option value="high">Price: High to Low</option>
            </select>
          </div>
        </div>
      </div>

      {/* Products Grid */}
      {filteredProducts.length === 0 ? (
        <div className="text-center py-12 bg-gray-50 rounded-lg">
          <svg xmlns="http://www.w3.org/2000/svg" className="h-16 w-16 text-gray-400 mx-auto mb-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9.172 16.172a4 4 0 015.656 0M9 10h.01M15 10h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
          </svg>
          <h3 className="text-lg font-medium text-gray-900">No products found</h3>
          <p className="mt-1 text-gray-500">Try changing your search or filter criteria.</p>
        </div>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
          {filteredProducts.map((product) => (
            <div key={product._id} className="bg-white rounded-lg overflow-hidden shadow-md hover:shadow-lg transition">
              <Link to={`/products/${product._id}`}>
                <img 
                  src={product.imageUrl} 
                  alt={product.name} 
                  className="w-full h-48 object-cover"
                />
                <div className="p-4">
                  <div className="flex justify-between items-start">
                    <h3 className="font-semibold text-lg text-gray-800">{product.name}</h3>
                    <span className="bg-green-100 text-green-800 text-xs py-1 px-2 rounded-full">
                      {product.category}
                    </span>
                  </div>
                  <p className="text-gray-600 text-sm mt-2 mb-4 line-clamp-2">{product.description}</p>
                  <div className="flex justify-between items-center">
                    <span className="font-bold text-green-700">Rs. {product.price.toFixed(2)}</span>
                    <span className={`text-sm ${product.stock > 0 ? 'text-green-600' : 'text-red-600'}`}>
                      {product.stock > 0 ? `${product.stock} in stock` : 'Out of stock'}
                    </span>
                  </div>
                </div>
              </Link>
              <div className="p-4 pt-0 flex justify-between border-t mt-4">
                <Link 
                  to={`/products/edit/${product._id}`}
                  className="text-blue-600 hover:text-blue-800"
                >
                  Edit
                </Link>
                <button 
                  className="text-red-600 hover:text-red-800"
                  onClick={() => {
                    if (window.confirm('Are you sure you want to delete this product?')) {
                      // In a real app, delete via API
                      // axios.delete(`http://localhost:5000/api/products/${product._id}`);
                      // Then update state
                      // setProducts(products.filter(p => p._id !== product._id));
                      console.log(`Delete product ${product._id}`);
                    }
                  }}
                >
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ProductsList;
