import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import api from '../config/api';

const BlogList = () => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedTag, setSelectedTag] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [tags, setTags] = useState([]);

  useEffect(() => {
    fetchPosts();
  }, []);

  // Extract all unique tags from blog posts
  useEffect(() => {
    if (posts.length > 0) {
      const allTags = new Set();
      posts.forEach(post => {
        if (post.tags && post.tags.length > 0) {
          post.tags.forEach(tag => allTags.add(tag));
        }
      });
      setTags(Array.from(allTags));
    }
  }, [posts]);

  const fetchPosts = async () => {
    try {
      const response = await api.get('/api/blog');
      setPosts(response.data.data || []);
      setLoading(false);
    } catch (err) {
      console.error('Error fetching blog posts:', err);
      setError('Failed to load blog posts. Please try again later.');
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const truncateContent = (content, maxLength = 150) => {
    // Remove HTML tags
    const plainText = content.replace(/<[^>]+>/g, '');

    if (plainText.length <= maxLength) return plainText;

    return plainText.substring(0, maxLength) + '...';
  };

  // Filter posts based on selected tag and search term
  const filteredPosts = posts.filter(post => {
    const matchesTag = selectedTag === '' || (post.tags && post.tags.includes(selectedTag));
    const matchesSearch = searchTerm === '' || 
      post.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      post.content.toLowerCase().includes(searchTerm.toLowerCase());

    return matchesTag && matchesSearch;
  });

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-green-700 mb-2">Ayurveda Blog</h1>
        <p className="text-gray-600">
          Discover insights, tips, and knowledge about Ayurvedic wellness and natural healing.
        </p>
      </div>

      <div className="flex flex-col md:flex-row gap-8">
        {/* Sidebar */}
        <div className="w-full md:w-1/4">
          <div className="bg-white p-6 rounded-lg shadow-md mb-6">
            <h2 className="text-lg font-semibold mb-4">Search</h2>
            <input
              type="text"
              placeholder="Search blog posts..."
              className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-green-500"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>

          <div className="bg-white p-6 rounded-lg shadow-md">
            <h2 className="text-lg font-semibold mb-4">Categories</h2>
            <div className="space-y-2">
              <button
                className={`block px-3 py-2 rounded-md w-full text-left ${
                  selectedTag === '' 
                    ? 'bg-green-100 text-green-700 font-medium' 
                    : 'text-gray-700 hover:bg-gray-100'
                }`}
                onClick={() => setSelectedTag('')}
              >
                All Categories
              </button>

              {tags.map(tag => (
                <button
                  key={tag}
                  className={`block px-3 py-2 rounded-md w-full text-left ${
                    selectedTag === tag 
                      ? 'bg-green-100 text-green-700 font-medium' 
                      : 'text-gray-700 hover:bg-gray-100'
                  }`}
                  onClick={() => setSelectedTag(tag)}
                >
                  {tag}
                </button>
              ))}
            </div>
          </div>
        </div>

        {/* Main content */}
        <div className="w-full md:w-3/4">
          {loading ? (
            <div className="flex justify-center items-center h-64">
              <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-green-500"></div>
            </div>
          ) : error ? (
            <div className="bg-red-100 text-red-700 p-4 rounded-lg">
              <p>{error}</p>
            </div>
          ) : filteredPosts.length === 0 ? (
            <div className="bg-white p-8 rounded-lg shadow-md text-center">
              <h2 className="text-xl font-semibold mb-2">No blog posts found</h2>
              <p className="text-gray-600">
                {searchTerm || selectedTag 
                  ? 'Try adjusting your search or category filters.' 
                  : 'Check back soon for new content!'}
              </p>
            </div>
          ) : (
            <div className="grid grid-cols-1 gap-8">
              {filteredPosts.map(post => (
                <div key={post._id} className="bg-white rounded-lg shadow-md overflow-hidden">
                  {post.featuredImage && (
                    <div className="w-full h-56 overflow-hidden">
                      <img 
                        src={post.featuredImage} 
                        alt={post.title} 
                        className="w-full h-full object-cover transition-transform duration-300 hover:scale-105"
                      />
                    </div>
                  )}
                  <div className="p-6">
                    <div className="flex flex-wrap gap-2 mb-4">
                      {post.tags && post.tags.map(tag => (
                        <span 
                          key={tag} 
                          className="bg-green-100 text-green-800 text-xs px-2 py-1 rounded"
                          onClick={() => setSelectedTag(tag)}
                          style={{ cursor: 'pointer' }}
                        >
                          {tag}
                        </span>
                      ))}
                    </div>
                    <h2 className="text-2xl font-bold mb-2 text-gray-800">{post.title}</h2>
                    <div className="text-gray-600 mb-4">
                      <span className="mr-4">
                        <i className="far fa-calendar-alt mr-1"></i>
                        {formatDate(post.createdAt)}
                      </span>
                      <span>
                        <i className="far fa-user mr-1"></i>
                        {post.author?.name || 'Admin'}
                      </span>
                    </div>
                    <p className="text-gray-700 mb-4">{truncateContent(post.content)}</p>
                    <div className="flex justify-between items-center">
                      <Link 
                        to={`/blog/${post._id}`}
                        className="text-green-600 font-medium hover:text-green-800"
                      >
                        Read More
                      </Link>
                      <div className="text-gray-500 text-sm">
                        <span className="mr-4">
                          <i className="far fa-eye mr-1"></i>
                          {post.viewCount} views
                        </span>
                        <span>
                          <i className="far fa-comment mr-1"></i>
                          {post.comments?.length || 0} comments
                        </span>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default BlogList;
