import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import AdminNavigation from './AdminNavigation';

const AdminFeedbackManager = () => {
  const [feedbacks, setFeedbacks] = useState([
    {
      id: 1,
      name: "John Smith",
      email: "john.smith@example.com",
      category: "Product",
      rating: 5,
      message: "I've been using the Brahmi hair oil for a month now, and I'm extremely impressed with the results. My hair feels stronger and there's noticeably less hair fall. I've recommended this to all my friends!",
      date: "2025-08-10",
      isArchived: false,
      isRead: true,
      response: "Thank you for your positive feedback! We're glad to hear that the Brahmi hair oil is working well for you."
    },
    {
      id: 2,
      name: "Sarah Johnson",
      email: "sarah.johnson@example.com",
      category: "Website",
      rating: 4,
      message: "The website is very well designed and easy to navigate. However, I think it would be useful to have product filtering by price range. Otherwise, great experience shopping here!",
      date: "2025-08-12",
      isArchived: false,
      isRead: false,
      response: ""
    },
    {
      id: 3,
      name: "Michael Rodriguez",
      email: "michael.r@example.com",
      category: "Delivery",
      rating: 3,
      message: "My order arrived safely, but it took longer than expected. The tracking information wasn't updated regularly which made it difficult to know when to expect delivery.",
      date: "2025-08-14",
      isArchived: false,
      isRead: false,
      response: ""
    },
    {
      id: 4,
      name: "Lisa Wong",
      email: "lisa.wong@example.com",
      category: "Product",
      rating: 2,
      message: "I purchased the digestive herbal tea but found it too bitter for my taste. Perhaps you could consider adding a milder version or providing better preparation instructions for those sensitive to strong flavors.",
      date: "2025-08-15",
      isArchived: false,
      isRead: false,
      response: ""
    },
    {
      id: 5,
      name: "Rajiv Patel",
      email: "rajiv.p@example.com",
      category: "CustomerService",
      rating: 5,
      message: "I had an issue with my order and contacted customer service. They were incredibly helpful and resolved my problem promptly. Excellent service!",
      date: "2025-08-16",
      isArchived: true,
      isRead: true,
      response: "We appreciate your kind words about our customer service team! We always strive to provide the best assistance to our valued customers."
    }
  ]);

  const [currentFeedback, setCurrentFeedback] = useState(null);
  const [responseText, setResponseText] = useState('');
  const [filter, setFilter] = useState('all'); // all, read, unread, archived
  const [sortBy, setSortBy] = useState('date'); // date, rating, category
  const [sortOrder, setSortOrder] = useState('desc'); // asc, desc

  // Function to toggle read status
  const toggleReadStatus = (id) => {
    setFeedbacks(feedbacks.map(feedback => 
      feedback.id === id ? { ...feedback, isRead: !feedback.isRead } : feedback
    ));
  };

  // Function to toggle archive status
  const toggleArchiveStatus = (id) => {
    setFeedbacks(feedbacks.map(feedback => 
      feedback.id === id ? { ...feedback, isArchived: !feedback.isArchived } : feedback
    ));
  };

  // Function to open response modal
  const openResponseModal = (feedback) => {
    setCurrentFeedback(feedback);
    setResponseText(feedback.response);
  };

  // Function to save response
  const saveResponse = () => {
    if (currentFeedback) {
      setFeedbacks(feedbacks.map(feedback => 
        feedback.id === currentFeedback.id 
          ? { ...feedback, response: responseText, isRead: true } 
          : feedback
      ));
      setCurrentFeedback(null);
      setResponseText('');
    }
  };

  // Function to delete feedback
  const deleteFeedback = (id) => {
    if (window.confirm('Are you sure you want to delete this feedback?')) {
      setFeedbacks(feedbacks.filter(feedback => feedback.id !== id));
    }
  };

  // Filter feedbacks based on selected filter
  const filteredFeedbacks = feedbacks.filter(feedback => {
    if (filter === 'all') return !feedback.isArchived;
    if (filter === 'read') return feedback.isRead && !feedback.isArchived;
    if (filter === 'unread') return !feedback.isRead && !feedback.isArchived;
    if (filter === 'archived') return feedback.isArchived;
    return true;
  });

  // Sort feedbacks based on selected criteria
  const sortedFeedbacks = [...filteredFeedbacks].sort((a, b) => {
    let comparison = 0;

    if (sortBy === 'date') {
      comparison = new Date(a.date) - new Date(b.date);
    } else if (sortBy === 'rating') {
      comparison = a.rating - b.rating;
    } else if (sortBy === 'category') {
      comparison = a.category.localeCompare(b.category);
    }

    return sortOrder === 'asc' ? comparison : -comparison;
  });

  // Function to get star rating display
  const renderStars = (rating) => {
    return Array(5).fill(0).map((_, index) => (
      <span key={index} className={index < rating ? "text-yellow-500" : "text-gray-300"}>â˜…</span>
    ));
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold text-green-800">Manage Feedback</h1>
        <div className="flex space-x-2">
          <select
            value={filter}
            onChange={(e) => setFilter(e.target.value)}
            className="px-3 py-2 border border-gray-300 rounded-md"
          >
            <option value="all">All Active</option>
            <option value="read">Read</option>
            <option value="unread">Unread</option>
            <option value="archived">Archived</option>
          </select>
          <select
            value={`${sortBy}-${sortOrder}`}
            onChange={(e) => {
              const [newSortBy, newSortOrder] = e.target.value.split('-');
              setSortBy(newSortBy);
              setSortOrder(newSortOrder);
            }}
            className="px-3 py-2 border border-gray-300 rounded-md"
          >
            <option value="date-desc">Newest First</option>
            <option value="date-asc">Oldest First</option>
            <option value="rating-desc">Highest Rating</option>
            <option value="rating-asc">Lowest Rating</option>
            <option value="category-asc">Category (A-Z)</option>
            <option value="category-desc">Category (Z-A)</option>
          </select>
        </div>
      </div>

      {sortedFeedbacks.length > 0 ? (
        <div className="bg-white rounded-lg shadow-md overflow-hidden">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Customer
                </th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Category
                </th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Rating
                </th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Date
                </th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Status
                </th>
                <th scope="col" className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Actions
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {sortedFeedbacks.map((feedback) => (
                <tr key={feedback.id} className={`hover:bg-gray-50 ${!feedback.isRead && !feedback.isArchived ? 'bg-green-50' : ''}`}>
                  <td className="px-6 py-4">
                    <div className="text-sm font-medium text-gray-900">{feedback.name}</div>
                    <div className="text-sm text-gray-500">{feedback.email}</div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className="px-2 inline-flex text-xs leading-5 font-semibold rounded-full bg-blue-100 text-blue-800">
                      {feedback.category}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="text-sm text-gray-900">{renderStars(feedback.rating)}</div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="text-sm text-gray-500">{feedback.date}</div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                      feedback.isArchived ? 'bg-gray-100 text-gray-800' : 
                      feedback.isRead ? 'bg-green-100 text-green-800' : 
                      'bg-yellow-100 text-yellow-800'
                    }`}>
                      {feedback.isArchived ? 'Archived' : feedback.isRead ? 'Read' : 'Unread'}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                    <button
                      onClick={() => openResponseModal(feedback)}
                      className="text-blue-600 hover:text-blue-900 mr-2"
                    >
                      {feedback.response ? 'Edit Response' : 'Respond'}
                    </button>
                    <button
                      onClick={() => toggleReadStatus(feedback.id)}
                      className={`${feedback.isRead ? 'text-yellow-600 hover:text-yellow-900' : 'text-green-600 hover:text-green-900'} mr-2`}
                    >
                      {feedback.isRead ? 'Mark Unread' : 'Mark Read'}
                    </button>
                    <button
                      onClick={() => toggleArchiveStatus(feedback.id)}
                      className={`${feedback.isArchived ? 'text-green-600 hover:text-green-900' : 'text-gray-600 hover:text-gray-900'} mr-2`}
                    >
                      {feedback.isArchived ? 'Unarchive' : 'Archive'}
                    </button>
                    <button
                      onClick={() => deleteFeedback(feedback.id)}
                      className="text-red-600 hover:text-red-900"
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <div className="bg-white rounded-lg shadow-md p-6 text-center">
          <p className="text-gray-500">No feedback found matching your criteria.</p>
        </div>
      )}

      {currentFeedback && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg shadow-lg max-w-2xl w-full mx-4">
            <div className="border-b p-4">
              <h3 className="text-lg font-medium">Customer Feedback</h3>
            </div>
            <div className="p-6">
              <div className="mb-6">
                <h4 className="font-semibold">{currentFeedback.name} <span className="font-normal text-gray-500">({currentFeedback.email})</span></h4>
                <div className="flex items-center my-2">
                  <span className="mr-2">Rating:</span>
                  {renderStars(currentFeedback.rating)}
                </div>
                <p className="mt-1 text-gray-500">Category: {currentFeedback.category}</p>
                <p className="mt-1 text-gray-500">Date: {currentFeedback.date}</p>
                <div className="mt-4 p-4 bg-gray-50 rounded-md">
                  <p className="text-gray-700">{currentFeedback.message}</p>
                </div>
              </div>

              <div>
                <label className="block text-gray-700 mb-2">Your Response</label>
                <textarea
                  value={responseText}
                  onChange={(e) => setResponseText(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
                  rows="4"
                  placeholder="Type your response here..."
                ></textarea>
              </div>
            </div>
            <div className="bg-gray-50 px-4 py-3 flex justify-end space-x-2 rounded-b-lg">
              <button
                onClick={() => setCurrentFeedback(null)}
                className="px-4 py-2 border border-gray-300 rounded-md hover:bg-gray-100"
              >
                Cancel
              </button>
              <button
                onClick={saveResponse}
                className="px-4 py-2 bg-green-700 text-white rounded-md hover:bg-green-800"
              >
                Save Response
              </button>
            </div>
          </div>
        </div>
      )}

      <div className="mt-8">
        <Link to="/feedback" className="text-green-700 hover:text-green-900 font-medium">
          View Public Feedback Page
        </Link>
      </div>
    </div>
  );
};

export default AdminFeedbackManager;
