import React, { useState, useEffect, useContext, useCallback } from 'react';
import { useParams, Link } from 'react-router-dom';
import api from '../../config/api';
import { AuthContext } from '../../context/AuthContext';

const BlogDetail = () => {
  const { id } = useParams();
  const { currentUser } = useContext(AuthContext);
  const [post, setPost] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [comment, setComment] = useState('');
  const [submitting, setSubmitting] = useState(false);
  const [commentError, setCommentError] = useState(null);

  const fetchPost = useCallback(async () => {
    try {
      const response = await api.get(`/api/blog/${id}`);
      setPost(response.data.data);
      setLoading(false);
    } catch (err) {
      console.error('Error fetching blog post:', err);
      setError('Failed to load blog post. Please try again later.');
      setLoading(false);
    }
  }, [id]);

  useEffect(() => {
    fetchPost();
  }, [fetchPost]);

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const handleCommentSubmit = async (e) => {
    e.preventDefault();
    if (!comment.trim()) return;

    setSubmitting(true);
    setCommentError(null);

    try {
      await api.post(`/api/blog/${id}/comments`, { content: comment });
      setComment('');
      fetchPost(); // Refresh to get the new comment
    } catch (err) {
      console.error('Error posting comment:', err);
      setCommentError(err.response?.data?.message || 'Failed to post comment. Please try again.');
    } finally {
      setSubmitting(false);
    }
  };

  const handleDeleteComment = async (commentId) => {
    if (!window.confirm('Are you sure you want to delete this comment?')) {
      return;
    }

    try {
      await api.delete(`/api/blog/${id}/comments/${commentId}`);
      fetchPost(); // Refresh to update comments
    } catch (err) {
      console.error('Error deleting comment:', err);
      alert('Failed to delete comment. Please try again.');
    }
  };

  if (loading) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="flex justify-center items-center h-64">
          <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-green-500"></div>
        </div>
      </div>
    );
  }

  if (error || !post) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="bg-red-100 text-red-700 p-6 rounded-lg">
          <h2 className="text-xl font-semibold mb-2">Error</h2>
          <p>{error || 'Blog post not found'}</p>
          <Link to="/blog" className="text-green-600 hover:underline mt-4 inline-block">
            Back to Blog
          </Link>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="mb-6">
        <Link to="/blog" className="text-green-600 hover:underline flex items-center">
          <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-1" viewBox="0 0 20 20" fill="currentColor">
            <path fillRule="evenodd" d="M9.707 16.707a1 1 0 01-1.414 0l-6-6a1 1 0 010-1.414l6-6a1 1 0 011.414 1.414L5.414 9H17a1 1 0 110 2H5.414l4.293 4.293a1 1 0 010 1.414z" clipRule="evenodd" />
          </svg>
          Back to Blog
        </Link>
      </div>

      <article className="bg-white rounded-lg shadow-lg overflow-hidden">
        {post.featuredImage && (
          <div className="w-full h-96 relative">
            <img 
              src={post.featuredImage} 
              alt={post.title} 
              className="w-full h-full object-cover"
            />
          </div>
        )}

        <div className="p-8">
          <header className="mb-8">
            <div className="flex flex-wrap gap-2 mb-4">
              {post.tags && post.tags.map(tag => (
                <Link 
                  key={tag} 
                  to={`/blog?tag=${tag}`}
                  className="bg-green-100 text-green-800 text-xs px-2 py-1 rounded hover:bg-green-200"
                >
                  {tag}
                </Link>
              ))}
            </div>

            <h1 className="text-4xl font-bold text-gray-800 mb-4">{post.title}</h1>

            <div className="flex items-center text-gray-600 mb-4">
              <div className="mr-6">
                <span className="mr-2">By:</span>
                <span className="font-medium">{post.author?.name || 'Admin'}</span>
              </div>
              <div className="mr-6">
                <span className="mr-2">Published:</span>
                <span>{formatDate(post.createdAt)}</span>
              </div>
              <div>
                <span className="mr-2">Views:</span>
                <span>{post.viewCount}</span>
              </div>
            </div>
          </header>

          <div 
            className="prose max-w-none"
            dangerouslySetInnerHTML={{ __html: post.content }}
          />
        </div>
      </article>

      <div className="mt-12">
        <h2 className="text-2xl font-bold mb-6 text-gray-800">Comments ({post.comments?.length || 0})</h2>

        {currentUser ? (
          <div className="bg-white rounded-lg shadow p-6 mb-8">
            <h3 className="text-lg font-medium mb-4">Leave a Comment</h3>

            {commentError && (
              <div className="bg-red-100 text-red-700 p-3 rounded mb-4">
                <p>{commentError}</p>
              </div>
            )}

            <form onSubmit={handleCommentSubmit}>
              <div className="mb-4">
                <textarea
                  rows="4"
                  placeholder="Write your comment here..."
                  className="w-full border border-gray-300 rounded-md px-4 py-2 focus:outline-none focus:ring-2 focus:ring-green-500"
                  value={comment}
                  onChange={(e) => setComment(e.target.value)}
                  required
                ></textarea>
              </div>
              <button
                type="submit"
                disabled={submitting || !comment.trim()}
                className="bg-green-600 text-white py-2 px-6 rounded-md hover:bg-green-700 disabled:opacity-50"
              >
                {submitting ? 'Posting...' : 'Post Comment'}
              </button>
            </form>
          </div>
        ) : (
          <div className="bg-yellow-50 text-yellow-800 p-4 rounded-lg mb-8">
            <p>
              Please <Link to="/login" className="text-green-600 hover:underline">log in</Link> to leave a comment.
            </p>
          </div>
        )}

        {post.comments && post.comments.length > 0 ? (
          <div className="space-y-6">
            {post.comments.map((comment) => (
              <div key={comment._id} className="bg-white rounded-lg shadow p-6">
                <div className="flex justify-between mb-2">
                  <span className="font-medium text-gray-800">{comment.user?.name || 'Unknown User'}</span>
                  <span className="text-gray-500 text-sm">{formatDate(comment.createdAt)}</span>
                </div>
                <p className="text-gray-700">{comment.content}</p>

                {/* Delete button for comment owner or admin */}
                {currentUser && (currentUser.id === comment.user?._id || currentUser.role === 'admin') && (
                  <div className="mt-2 flex justify-end">
                    <button
                      onClick={() => handleDeleteComment(comment._id)}
                      className="text-red-600 hover:text-red-800 text-sm"
                    >
                      Delete
                    </button>
                  </div>
                )}
              </div>
            ))}
          </div>
        ) : (
          <div className="bg-gray-50 p-6 rounded-lg text-center">
            <p className="text-gray-600">No comments yet. Be the first to share your thoughts!</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default BlogDetail;
