              {
                productId: {
                  _id: '5',
                  name: 'Herbal Hair Oil',
                  price: 950.00
                },
                quantity: 1,
                price: 950.00
              }
            ],
            totalAmount: 3351.00,
            status: 'Delivered',
            orderDate: '2023-08-10T15:30:00.000Z',
            deliveryAddress: 'No 10, Galle Road, Colombo'
          },
          {
            _id: '2',
            customerId: {
              _id: '2',
              name: 'Kamala Silva',
              email: 'kamala@example.com'
            },
            items: [
              {
                productId: {
                  _id: '3',
                  name: 'Digestive Herbal Tea',
                  price: 850.00
                },
                quantity: 1,
                price: 850.00
              }
            ],
            totalAmount: 850.00,
            status: 'Processing',
            orderDate: '2023-08-12T09:45:00.000Z',
            deliveryAddress: '25 Kandy Road, Kurunegala'
          },
          {
            _id: '3',
            customerId: {
              _id: '3',
              name: 'Amal Fernando',
              email: 'amal@example.com'
            },
            items: [
              {
                productId: {
                  _id: '2',
                  name: 'Ayurvedic Face Cream',
                  price: 1500.00
                },
                quantity: 1,
                price: 1500.00
              },
              {
                productId: {
                  _id: '8',
                  name: 'Aloe Vera Gel',
                  price: 750.00
