import React, { useContext, useEffect, useState } from 'react';
import { Navigate, Outlet, useLocation, useNavigate } from 'react-router-dom';
import { AuthContext } from '../../context/AuthContext';
import { useModal } from '../../context/ModalContext';

// Component to protect routes based on authentication and role
const PrivateRoute = ({ allowedRoles }) => {
  const context = useContext(AuthContext);
  const { currentUser, token, loading } = context || { currentUser: null, token: null, loading: false };
  const location = useLocation();
  const navigate = useNavigate();
  const { openModal } = useModal();
  const [modalShown, setModalShown] = useState(false);

  // If still loading auth state, don't render anything yet
  if (loading) {
    return (
      <div className="flex justify-center items-center h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-green-500"></div>
      </div>
    );
  }

  // Handle access control with a single useEffect
  useEffect(() => {
    // First check: Handle unauthenticated users
    if (!token || !currentUser) {
      if (!modalShown) {
        setModalShown(true);
        openModal({
          title: 'Authentication Required',
          message: 'You need to log in to access this page.',
          confirmText: 'Log In',
          type: 'info',
          onConfirm: () => navigate('/login', { state: { from: location } })
        });
      }
    } 
    // Second check: Handle unauthorized access
    else if (allowedRoles && !allowedRoles.includes(currentUser?.role)) {
      if (!modalShown) {
        setModalShown(true);
        openModal({
          title: 'Access Denied',
          message: 'You do not have permission to access this page.',
          confirmText: 'OK',
          type: 'warning',
          onConfirm: () => navigate('/unauthorized')
        });
      }
    }
  }, [token, currentUser, allowedRoles, location, navigate, openModal, modalShown]);

  // Check if user is authenticated
  if (!token || !currentUser) {
    // Redirect to login page, but save the location they were trying to access
    return <Navigate to="/login" state={{ from: location }} replace />;
  }

  // If roles are specified, check if user has required role
  if (allowedRoles && !allowedRoles.includes(currentUser?.role)) {
    // Redirect to unauthorized page
    return <Navigate to="/unauthorized" replace />;
  }

  // User is authenticated and has the required role, render the protected component
  return <Outlet />;
};

export default PrivateRoute;
